const categories = ["Action", "Horror", "Comedy", "Sci-Fi", "Fantasy", "Romance"];

export default function BookApp() {
  const [selectedCategory, setSelectedCategory] = useState("Action");
  const [search, setSearch] = useState("");
  const [selectedBook, setSelectedBook] = useState(null);
  const [favorites, setFavorites] = useState([]);

  const filteredBooks = books.filter(
    (book) =>
      book.category === selectedCategory &&
      book.title.toLowerCase().includes(search.toLowerCase())
  );

  const toggleFavorite = (bookId) => {
    if (favorites.includes(bookId)) {
      setFavorites(favorites.filter((id) => id !== bookId));
    } else {
      setFavorites([...favorites, bookId]);
    }
  };

  return (
    <View style={styles.container}>
      {/* Search Bar */}
      <TextInput
        style={styles.searchBar}
        placeholder="Search Novels..."
        value={search}
        onChangeText={(text) => setSearch(text)}
      />

      {/* Categories */}
      <View style={styles.categoryContainer}>
        {categories.map((cat) => (
          <TouchableOpacity
            key={cat}
            onPress={() => setSelectedCategory(cat)}
            style={[
              styles.categoryButton,
              selectedCategory === cat && styles.categoryButtonActive,
            ]}
          >
            <Text
              style={[
                styles.categoryText,
                selectedCategory === cat && styles.categoryTextActive,
              ]}
            >
              {cat}
            </Text>
          </TouchableOpacity>
        ))}
      </View>

      {/* Book List */}
      <FlatList
        data={filteredBooks}
        keyExtractor={(item) => item.id}
        horizontal
        showsHorizontalScrollIndicator={false}
        renderItem={({ item }) => (
          <TouchableOpacity
            style={styles.bookCard}
            onPress={() => setSelectedBook(item)}
          >
            <Image source={{ uri: item.cover }} style={styles.bookImage} />
            <Text style={styles.bookTitle} numberOfLines={1}>
              {item.title}
            </Text>
            <Text style={styles.bookAuthor}>{item.author}</Text>

            {/* Progress */}
            <View style={styles.progressBar}>
              <View
                style={[
                  styles.progressFill,
                  { width: `${(item.progress / item.pages) * 100}%` },
                ]}
              />
            </View>
            <Text style={styles.progressText}>
              {Math.round((item.progress / item.pages) * 100)}% complete
            </Text>

            {/* Favorite Button */}
            <TouchableOpacity
              onPress={() => toggleFavorite(item.id)}
              style={styles.favoriteButton}
            >
              <Text style={{ color: favorites.includes(item.id) ? "red" : "gray" }}>
                ♥
              </Text>
            </TouchableOpacity>
          </TouchableOpacity>
        )}
      />

      {/* Modal for book details */}
      {selectedBook && (
        <Modal
          transparent
          animationType="slide"
          visible={!!selectedBook}
          onRequestClose={() => setSelectedBook(null)}
        >
          <View style={styles.modalContainer}>
            <View style={styles.modalContent}>
              <Image
                source={{ uri: selectedBook.cover }}
                style={styles.modalImage}
              />
              <Text style={styles.modalTitle}>{selectedBook.title}</Text>
              <Text style={styles.modalAuthor}>{selectedBook.author}</Text>
              <Text style={styles.modalDescription}>{selectedBook.description}</Text>

              <Pressable
                style={styles.closeButton}
                onPress={() => setSelectedBook(null)}
              >
                <Text style={{ color: "#fff" }}>Close</Text>
              </Pressable>
            </View>
          </View>
        </Modal>
      )}
    </View>
  );
}

